mapper.select:
select count(1)
from tb_news_view
where cust_id = #{custId} and title_id = #{titleId}

mapper.insert:
insert into tb_news_view (id, title_id,
  cust_id, read_times, created_at, updated_at
  )
values (#{id,jdbcType=BIGINT}, #{titleId,jdbcType=VARCHAR},
  #{custId,jdbcType=VARCHAR}, 1, now(), now()
  )

mapper.update:
update tb_news_view
read_times = read_times + 1, updated_at = now()
cust_id = #{custId} and title_id = #{titleId}
