mapper.select:
select 

from tb_trade
where id = #{id,jdbcType=BIGINT}
  and order_id = #{orderId,jdbcType=VARCHAR}

mapper.delete:
delete from tb_trade
where id = #{id,jdbcType=BIGINT}
  and order_id = #{orderId,jdbcType=VARCHAR}

mapper.insert:
insert into tb_trade (id, order_id, state, 
  msg, created_at, updated_at
  )
values (#{id,jdbcType=BIGINT}, #{orderId,jdbcType=VARCHAR}, #{state,jdbcType=INTEGER}, 
  #{msg,jdbcType=VARCHAR}, #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP}
  )

mapper.update:
update tb_trade
set state = #{state,jdbcType=INTEGER},
  msg = #{msg,jdbcType=VARCHAR},
  created_at = #{createdAt,jdbcType=TIMESTAMP},
  updated_at = #{updatedAt,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
  and order_id = #{orderId,jdbcType=VARCHAR}
